public class ASMD {
	int num1,num2;
	ASMD(int n1, int n2){
		num1 = n1;
		num2 = n2;
	}
	public void add() {
		// TODO Auto-generated method stub
		System.out.println(num1+num2);
	}
	public void sub() {
		// TODO Auto-generated method stub
		System.out.println(num1-num2);
	}
	public void mul() {
		// TODO Auto-generated method stub
		System.out.println(num1*num2);
	}
	public void div() {
		// TODO Auto-generated method stub
		System.out.println(num1/num2);
	}
	public void mod() {
		// TODO Auto-generated method stub
		System.out.println(num1%num2);
	}
}